import { Injectable } from '@angular/core';
import {HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class ProductsService {

constructor(private http : HttpClient) { }


getAllProducts(){
  return this.http.get("https://api.storerestapi.com/products")

}

getAllCategories(){
  return this.http.get("https://api.storerestapi.com/categories/")

}

getProductsByCategory(keyword:string){
  return this.http.get(`https://api.storerestapi.com/category/`+ keyword)

}

getProductsById(id:any){
  return this.http.get(`https://api.storerestapi.com/products/`+ id)

}




}
